import React, { Component } from 'react'
import { connect } from 'react-redux';
import { InputGroup, FormControl, Button } from 'react-bootstrap'
import { API_BASE_URL } from './constants'
import { Redirect } from 'react-router-dom'
import './Login.css'

class Login extends Component {

    state = {
        usernameOrEmail: "",
        password: "",
        shouldRedirect: false
    };

    login = () => {
        fetch(API_BASE_URL + '/api/auth/signin', {
            method: 'post',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ usernameOrEmail: this.state.usernameOrEmail, password: this.state.password })
        }).then(res => res.json())
            .then(res => {
                console.log(res)
                if (res.accessToken && res.username) {

                    this.props.setUser({ token: res.accessToken, username: res.username })
                    this.setState({ shouldRedirect: true })
                }
            }
            );
    }

