import React, { Component } from 'react'
import { connect } from 'react-redux';
import { API_BASE_URL } from './constants'
import { Redirect } from 'react-router-dom'
import './Login.css'

class Login extends Component {

    state = {
        usernameOrEmail: '',
        loginPassword: '',
        username: '',
        password: '',
        email: '',
        area: '',
        city: '',
        state: '',
        country: '',
        shouldRedirect: false
    };

    login = event => {
        event.preventDefault();

        fetch(API_BASE_URL + '/api/auth/signin', {
            method: 'post',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ usernameOrEmail: this.state.usernameOrEmail, password: this.state.loginPassword })
        }).then(res => res.json())
            .then(res => {
                console.log(res)
                if (res.accessToken && res.username) {

                    this.props.setUser({ token: res.accessToken, username: res.username })
                    this.setState({ shouldRedirect: true })
                }
            }
            );
    }

    signUp = event => {
        event.preventDefault();

        fetch(API_BASE_URL + '/api/auth/signup', {
            method: 'post',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ username: this.state.username, password: this.state.password, email: this.state.email, area: this.state.area, city: this.state.city, state: this.state.state, country: this.state.country })
        }).then(res => res.json())
            .then(res => {
                console.log(res)
                // if (res.accessToken && res.username) {

                //     this.props.setUser({ token: res.accessToken, username: res.username })
                //     this.setState({ shouldRedirect: true })
                // }
            }
            );
    }

    // write function to call signup (google about fetch)


    // write function to call login (this will return accessToken and username in response. Store them in redux)
    // like this
    // if (accessToken && username) {
    //     this.props.setUser({ token: accessToken, username: username })
    // }
    // we save the token here to redux so that in some other file, when calling an authenticated api, we can get the token easily from redux (refer Library.js and Books.js)
    // google about react redux

    render() {
        const redirect = this.state.shouldRedirect ? <Redirect to="/books" /> : null
        return (

            <div>
                {redirect}

                <div class="container px-4 px-lg-5">
                    <div class="row gx-4 gx-lg-5 justify-content-center">
                        <div class="col-lg-8 col-xl-6 text-center">
                            <h2 class="mt-0">Log In</h2>
                            <hr class="divider" />
                            <p class="text-muted mb-5">Connect with book lovers like you!</p>
                        </div>
                    </div>
                    <div className="row gx-4 gx-lg-5 justify-content-center mb-5">
                        <div className="col-lg-6">
                            <form id="loginForm" onSubmit={this.login}>
                                <div className="form-floating mb-3">
                                    <input className="form-control" id="email" type="text" placeholder="name@example.com" value={this.state.usernameOrEmail}
                        onChange={e => this.setState({ usernameOrEmail: e.target.value })}/>
                                    <label for="email">Username or Email address</label>
                                    {/* <div className="invalid-feedback" data-sb-feedback="email:required">An email is required.</div>
                                    <div className="invalid-feedback" data-sb-feedback="email:email">Email is not valid.</div> */}
                                </div>
                                <div className="form-floating mb-3">
                                    <input className="form-control" id="phone" type="password" placeholder="Password" value={this.state.loginPassword}
                        onChange={e => this.setState({ loginPassword: e.target.value })}/>
                                    <label for="phone">Password</label>
                                </div>
                                <div className="d-grid"><button className="btn btn-primary btn-xl" id="loginButton" type="submit">Submit</button></div>
                            </form>
                        </div>
                    </div>
                    <div className="row gx-4 gx-lg-5 justify-content-center">
                        <div className="col-lg-4 text-center mb-5 mb-lg-0">
                            <h2 class="mt-0">Sign Up</h2>
                            <hr class="divider" />
                            <p class="text-muted mb-5">It's quick and Easy.</p>
                        </div>
                    </div>
                    <div className="row gx-4 gx-lg-5 justify-content-center mb-5">
                        <div className="col-lg-6">
                            <form id="signupForm" onSubmit={this.signUp}>
                                <div className="form-floating mb-3">
                                    <input className="form-control" id="email" type="text" placeholder="name@example.com" data-sb-validations="required,email" />
                                    <label for="email">Username</label>
                                    <div className="invalid-feedback" data-sb-feedback="email:required">An email is required.</div>
                                    <div className="invalid-feedback" data-sb-feedback="email:email">Email is not valid.</div>
                                </div>
                                <div className="form-floating mb-3">
                                    <input className="form-control" id="email" type="email" placeholder="name@example.com" data-sb-validations="required,email" />
                                    <label for="email">Email</label>
                                    <div className="invalid-feedback" data-sb-feedback="email:required">An email is required.</div>
                                    <div className="invalid-feedback" data-sb-feedback="email:email">Email is not valid.</div>
